sql.where:
1=1

  and c.product_no = #{productNo,jdbcType=VARCHAR}


  and c.customer_no=#{customerNo,jdbcType=VARCHAR}


  and c.customer_pd_name LIKE CONCAT('%',#{customerPdName,jdbcType=VARCHAR},'%')

mapper.select:
select , upc.identifying_code,sku.min_uint,sku.brand_id,sku.brand_name,ca.class_name from sku_customer c left join sku_core sku on c.product_no=sku.product_no left join sku_upc upc on c.product_no=upc.product_no left join category ca on sku.category_no=ca.class_code
and sku.brand_id=#{brandId,jdbcType=INTEGER}
and sku.brand_name = #{brandName,jdbcType=VARCHAR}

select
c.id id,c.product_no productNo,c.customer_pd_name customerPdName, c.customer_pd_no customerPdNo,c.brand_id brandId,
c.brand_name brandName,c.customer_pd_rule customerPdRule,c.customer_pd_size customerPdSize,c.min_uint minUint,
sku.category_no categoryNo,sku.lv2_category_no lv2CategoryNo,sku.lv1_category_no lv1CategoryNo,
sku.category_id categoryId,sku.lv1_category_id lv1CategoryId,sku.lv2_category_id lv2CategoryId,
sku.category_sp_id categorySpId,sku.lv1_category_sp_id lv1CategorySpId,sku.lv2_category_sp_id lv2CategorySpId,
sku.category_sp_no categorySpNo,sku.lv2_category_sp_no lv2CategorySpNo,sku.lv1_category_sp_no lv1CategorySpNo,
sku.ean13_code ean13Code,sku.mnemonic_code mnemonicCode,
sku.channel,ent.ent_name entName,ent.ent_number customerNo
from sku_customer c left join sku_core sku on c.product_no=sku.product_no
left join enterprise_info ent on ent.ent_number=c.customer_no

  1=1

    and c.product_no =#{productNo}


    and c.product_no LIKE CONCAT('%',#{productNoLike,jdbcType=VARCHAR},'%')


    and c.id =#{id}


    and sku.channel =#{channel}


    and c.brand_name=#{brandName}


    and c.customer_pd_name LIKE CONCAT('%',#{customerPdName,jdbcType=VARCHAR},'%')


    and c.customer_pd_size =#{size}


    and c.customer_pd_size  LIKE CONCAT('%',#{sizeLike,jdbcType=VARCHAR},'%')


    and c.customer_pd_rule like concat('%',#{customerPdRuleLike,jdbcType=VARCHAR},'%')


    and c.customer_pd_rule = #{customerPdRule,jdbcType=VARCHAR}


    and ent.ent_name LIKE CONCAT('%',#{entName,jdbcType=VARCHAR},'%')


    and ent.ent_name LIKE CONCAT('%',#{entName,jdbcType=VARCHAR},'%')


    and sku.lv1_category_no = #{lv1CategoryNo,jdbcType=VARCHAR}


    and sku.lv2_category_no = #{lv2CategoryNo,jdbcType=VARCHAR}


    and sku.category_no = #{categoryNo,jdbcType=VARCHAR}


    and sku.lv1_category_sp_no = #{lv1CategorySpNo,jdbcType=VARCHAR}


    and sku.lv2_category_sp_no = #{lv2CategorySpNo,jdbcType=VARCHAR}


    and sku.category_sp_no = #{categorySpNo,jdbcType=VARCHAR}


order  by  c.create_time desc
